<script>
import bodyLock from '~/mixins/bodyLock'

export default {
  mixins: [bodyLock],
  data() {
    return {
      swiperOptions: {
        navigation: {
          nextEl: '.main-banner__btn-next.swiper-button-next:not(._go-first)',
          prevEl: '.main-banner__btn-prev.swiper-button-prev',
        },
        effect: 'slide',
        initialSlide: 0,
        autoplay: {
          delay: 6000,
        },
        simulateTouch: false,
        hashNavigation: {
          watchState: true,
        },
        pagination: {
          el: '#paginationBanner',
          clickable: true,
          type: 'bullets',
        },
        on: {
          afterInit(swiper) {
            swiper.slides.forEach((element) => {
              element.classList.remove('slide-animation')
            })
            const activeSlide = swiper.slides[swiper.activeIndex];
            activeSlide.classList.add('slide-animation');
          },
        },
      },
      videoOptions: {
        autoplay: false,
        controlBar: {
          playbackRateMenuButton: false,
          volumeMenuButton: false,
          remainingTimeDisplay: false,
        },
        language: 'ru',
        defaultSrcReId: 3,
        playsinline: true,
        poster: '/videos/skillline-poster.jpg',
        source: {
          src: '/videos/skillline.webm',
          type: 'video/webm',
        },
      },
      indexSwiperSlide: 0,
      loader: false,
      isLoading: true,
      timerPlay: null,
    }
  },
  computed: {
    player() {
      return this.$refs.playerHeader.player
    },
  },
  mounted() {
    this.$nextTick().then(() => {
      this.isLoading = false
      window.player = this.player
      document.addEventListener('fullscreenchange', () => {
        this.loader = !!document.fullscreenElement
        if (!this.loader && this.player) {
          this.player.pause()
          if (this.$refs.sliderMain) {
            this.$refs.sliderMain.$swiper?.slideTo(3, 0)
          }
        }
      })
    })
  },
  methods: {
    handlerInit(swiper) {
      this.indexSwiperSlide = swiper.activeIndex;
      swiper.slides.forEach((element) => {
        element.classList.remove('slide-animation');
      });
      const activeSlide = swiper.slides[swiper.activeIndex];
      activeSlide.classList.add('slide-animation');
    },
    goFirstSlide() {
      if (this.$refs.sliderMain) {
        console.log(this.$refs.sliderMain.swiper);
        this.$refs.sliderMain.swiper?.slideTo(0, 0);
      }
    },
    openFeedbackModal() {
      this.$store.commit('modals/setShowModalFeedback', true)
      this.toggleLockBody(true)
      if (this.$yandexMetrika) {
        this.$yandexMetrika.reachGoal('click_form')
      }
    },
    openInFullscreen() {
      const elem = this.player
      if (!elem || elem.isFullscreen()) {
        return
      }

      if (elem.requestFullscreen) {
        elem.requestFullscreen()
      } else if (elem.webkitRequestFullscreen) {
        /* Safari */
        elem.webkitRequestFullscreen()
      } else if (elem.webkitEnterFullscreen) {
        /* Safari */
        elem.webkitEnterFullscreen()
      } else if (elem.msRequestFullscreen) {
        /* IE11 */
        elem.msRequestFullscreen()
      } else if (elem.mozRequestFullScreen) {
        /* Mozila */
        elem.mozRequestFullScreen()
      }
      // clearTimeout(this.timerPlay);
      // this.timerPlay = setTimeout(() => {
      //   elem.play();
      // }, 100);
    },
  },
}
</script>

